import React, { useState, useEffect, useCallback } from 'react';
import { AutoSizer, List } from 'react-virtualized';
import { connectToWebSocket, subscribeToTokens, unsubscribeFromTokens } from '../services/websocketService';
import './OptionChain.css';

const OptionChain = () => {
  const [optionChainData, setOptionChainData] = useState(null);
  const [priceUpdates, setPriceUpdates] = useState({});
  const [underlyingPrice, setUnderlyingPrice] = useState(0);
  const [underlyingChange, setUnderlyingChange] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const handleInitialData = useCallback((data) => {
    setOptionChainData(data);
    setUnderlyingPrice(data.underlyingPrice);
    setUnderlyingChange(
      ((data.underlyingPrice / data.yesterdayUnderlyingPrice - 1) * 100).toFixed(2)
    );
    setIsLoading(false);

    // Subscribe to all tokens
    const tokens = [
      'UNDERLYING',
      ...data.strikes.flatMap(strike => [strike.call.token, strike.put.token])
    ];
    subscribeToTokens(tokens);
  }, []);

  const handlePriceUpdate = useCallback((data) => {
    setPriceUpdates(prev => ({ ...prev, ...data }));

    if (data['UNDERLYING']) {
      setUnderlyingPrice(data['UNDERLYING'].currentPrice);
      setUnderlyingChange(data['UNDERLYING'].percentChange);
    }
  }, []);

  const handleMarketState = useCallback((data) => {
    setPriceUpdates(prev => ({ ...prev, ...data }));

    if (data['UNDERLYING']) {
      setUnderlyingPrice(data['UNDERLYING'].currentPrice);
      setUnderlyingChange(data['UNDERLYING'].percentChange);
    }
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Get initial data from WebSocket
        connectToWebSocket({
          onInitialData: handleInitialData,
          onPriceUpdate: handlePriceUpdate,
          onMarketState: handleMarketState,
          onError: (error) => {
            console.error('WebSocket error:', error);
            setError('Failed to connect to server');
          }
        });
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Failed to fetch data');
        setIsLoading(false);
      }
    };

    fetchData();

    return () => {
      if (optionChainData) {
        const tokens = [
          'UNDERLYING',
          ...optionChainData.strikes.flatMap(strike => [strike.call.token, strike.put.token])
        ];
        unsubscribeFromTokens(tokens);
      }
    };
  }, [handleInitialData, handlePriceUpdate, handleMarketState, optionChainData]);

  const renderRow = ({ index, key, style }) => {
    if (!optionChainData) return null;

    const strikeData = optionChainData.strikes[index];
    const underlyingPriceValue = underlyingPrice || 0;
    const atTheMoneyDiff = Math.abs(strikeData.strike - underlyingPriceValue);
    const isAtTheMoney = atTheMoneyDiff < 50; // within 50 points of current price

    const callData = priceUpdates[strikeData.call.token] || {};
    const putData = priceUpdates[strikeData.put.token] || {};

    const callPrice = callData.currentPrice || strikeData.call.yesterdayPrice;
    const callChange = callData.percentChange || 0;

    const putPrice = putData.currentPrice || strikeData.put.yesterdayPrice;
    const putChange = putData.percentChange || 0;

    const callInTheMoney = strikeData.strike < underlyingPriceValue;
    const putInTheMoney = strikeData.strike > underlyingPriceValue;

    return (
      <div key={key} style={style} className={`option-row ${isAtTheMoney ? 'at-the-money' : ''}`}>
        <div className={`option-cell call ${callInTheMoney ? 'in-the-money' : ''}`}>
          <div className="option-price">{callPrice.toFixed(2)}</div>
          <div className={`option-change ${callChange >= 0 ? 'positive' : 'negative'}`}>
            {callChange >= 0 ? '+' : ''}{callChange.toFixed(2)}%
          </div>
        </div>
        <div className="option-cell strike">{strikeData.strike.toFixed(2)}</div>
        <div className={`option-cell put ${putInTheMoney ? 'in-the-money' : ''}`}>
          <div className="option-price">{putPrice.toFixed(2)}</div>
          <div className={`option-change ${putChange >= 0 ? 'positive' : 'negative'}`}>
            {putChange >= 0 ? '+' : ''}{putChange.toFixed(2)}%
          </div>
        </div>
      </div>
    );
  };

  if (isLoading) {
    return <div className="loading">Loading option chain data...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="option-chain-container">
      <div className="underlying-info">
        <div className="underlying-price">
          Underlying Price: <span>{underlyingPrice.toFixed(2)}</span>
        </div>
        <div className={`underlying-change ${underlyingChange >= 0 ? 'positive' : 'negative'}`}>
          Change: {underlyingChange >= 0 ? '+' : ''}{underlyingChange}%
        </div>
      </div>

      <div className="option-chain-header">
        <div className="option-cell">CALL</div>
        <div className="option-cell">STRIKE</div>
        <div className="option-cell">PUT</div>
      </div>

      <div className="option-chain-list">
        <AutoSizer>
          {({ width, height }) => (
            <List
              width={width}
              height={height}
              rowHeight={60}
              rowCount={optionChainData ? optionChainData.strikes.length : 0}
              rowRenderer={renderRow}
              overscanRowCount={5}
            />
          )}
        </AutoSizer>
      </div>
    </div>
  );
};

export default OptionChain;
