import React, { useState, useEffect, useCallback } from 'react';
import { Box, Typography, CircularProgress, Chip, Switch, FormControlLabel } from '@mui/material';
import { AutoSizer, List } from 'react-virtualized';
import { connectToWebSocket, subscribeToTokens, unsubscribeFromTokens } from '../services/websocketService';
import './OptionChain.css';

const OptionChain = ({ darkMode, toggleDarkMode }) => {
  const [optionChainData, setOptionChainData] = useState(null);
  const [priceUpdates, setPriceUpdates] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [reconnecting, setReconnecting] = useState(false);
  const [underlyingPrice, setUnderlyingPrice] = useState(null);
  const [optionData, setOptionData] = useState(null);

  const handleInitialData = useCallback((data) => {
    console.log('Received initial option chain data');
    setOptionChainData(data);
    setIsLoading(false);
  }, []);

  // Handle individual price updates from WebSocket
  const handlePriceUpdate = useCallback((updates) => {
    if (!updates) return;

    // Update underlying price if present
    if (updates['UNDERLYING']) {
      setUnderlyingPrice(updates['UNDERLYING'].price);
    }

    // Update options data
    setOptionData(prevData => {
      if (!prevData || !prevData.strikes) return prevData;

      // Create a copy of the data to modify
      const newData = { ...prevData };
      newData.strikes = prevData.strikes.map(strike => {
        const updatedStrike = { ...strike };

        // Update call option if we have an update for it
        if (updates[strike.call.token]) {
          updatedStrike.call = {
            ...strike.call,
            ...updates[strike.call.token],
            highlight: true
          };
        }

        // Update put option if we have an update for it
        if (updates[strike.put.token]) {
          updatedStrike.put = {
            ...strike.put,
            ...updates[strike.put.token],
            highlight: true
          };
        }

        return updatedStrike;
      });

      return newData;
    });

    // Clear highlights after 1 second
    setTimeout(() => {
      setOptionData(prevData => {
        if (!prevData || !prevData.strikes) return prevData;

        const newData = { ...prevData };
        newData.strikes = prevData.strikes.map(strike => {
          return {
            ...strike,
            call: { ...strike.call, highlight: false },
            put: { ...strike.put, highlight: false }
          };
        });

        return newData;
      });
    }, 1000);
  }, []);

  const handleConnectionStatus = useCallback((status) => {
    console.log('Connection status update:', status);
    setIsConnected(status);
    if (status) {
      setReconnecting(false);
    }
  }, []);

  const handleReconnecting = useCallback((attempt, maxAttempts) => {
    console.log(`Reconnecting: Attempt ${attempt} of ${maxAttempts}`);
    setReconnecting(true);
  }, []);

  // Subscribe to a specific option
  const handleSubscribe = useCallback((token) => {
    console.log('Subscribing to token:', token);
    subscribeToTokens([token]);
  }, []);

  // Unsubscribe from a specific option
  const handleUnsubscribe = useCallback((token) => {
    console.log('Unsubscribing from token:', token);
    unsubscribeFromTokens([token]);
  }, []);

  useEffect(() => {
    let mounted = true;
    setIsLoading(true);

    // Connect to WebSocket
    connectToWebSocket({
      onInitialData: (data) => {
        if (mounted) {
          console.log('Option Chain: Received initial data', data);
          handleInitialData(data);
          // Automatically subscribe to underlying and some options for demo
          const defaultTokens = ['UNDERLYING'];
          // Add some strikes around the current price
          if (data && data.strikes && data.strikes.length > 0) {
            const midIndex = Math.floor(data.strikes.length / 2);
            for (let i = Math.max(0, midIndex - 5); i < Math.min(data.strikes.length, midIndex + 5); i++) {
              defaultTokens.push(data.strikes[i].call.token);
              defaultTokens.push(data.strikes[i].put.token);
            }
          }
          // Subscribe to these tokens
          subscribeToTokens(defaultTokens);
        }
      },
      onPriceUpdate: (data) => {
        if (mounted) {
          console.log('Option Chain: Received price update', data);
          handlePriceUpdate(data);
        }
      },
      onConnectionStatus: (status) => {
        if (mounted) handleConnectionStatus(status);
      },
      onReconnecting: (attempt, maxAttempts) => {
        if (mounted) handleReconnecting(attempt, maxAttempts);
      },
      onError: (error) => {
        console.error('Option Chain: WebSocket error:', error);
        if (mounted) setError(`Connection error: ${error.message || error}`);
        setIsLoading(false);
      }
    });

    return () => {
      mounted = false;
    };
  }, [handleInitialData, handlePriceUpdate, handleConnectionStatus, handleReconnecting]);

  // Helper function to format price
  const formatPrice = (price) => {
    return price !== undefined ? price.toFixed(2) : '-';
  };

  // Helper function to get price update data
  const getPriceData = (token) => {
    return priceUpdates[token] || null;
  };

  // Helper function to determine strike style
  const getStrikeStyle = (strike, underlyingPrice) => {
    if (!underlyingPrice) return '';

    if (Math.abs(strike - underlyingPrice) < 5) {
      return 'at-the-money';
    } else if (strike < underlyingPrice) {
      return 'in-the-money-call';
    } else {
      return 'out-of-the-money-call';
    }
  };

  // Render row function for virtualized list
  const renderRow = ({ index, key, style }) => {
    if (!optionData || !optionData.strikes) return null;

    const strikeData = optionData.strikes[index];
    if (!strikeData) return null;

    const underlyingPrice = optionChainData.underlyingPrice;
    const strikeStyle = getStrikeStyle(strikeData.strike, underlyingPrice);

    const callPriceData = strikeData.call;
    const putPriceData = strikeData.put;

    const callPrice = callPriceData.price;
    const callPriceChange = callPriceData.percentChange;
    const callUpdated = callPriceData.highlight;

    const putPrice = putPriceData.price;
    const putPriceChange = putPriceData.percentChange;
    const putUpdated = putPriceData.highlight;

    return (
      <div key={key} style={style} className={`option-row ${strikeStyle} ${darkMode ? 'dark-mode' : ''}`}>
        <div className={`price-cell ${callPriceChange >= 0 ? 'positive' : 'negative'} ${callUpdated ? 'updated' : ''}`}>
          {formatPrice(callPrice)}
          <span className="percent-change">
            {callPriceChange >= 0 ? '+' : ''}{callPriceChange.toFixed(2)}%
          </span>
        </div>
        <div className="strike-cell">
          {strikeData.strike}
        </div>
        <div className={`price-cell ${putPriceChange >= 0 ? 'positive' : 'negative'} ${putUpdated ? 'updated' : ''}`}>
          {formatPrice(putPrice)}
          <span className="percent-change">
            {putPriceChange >= 0 ? '+' : ''}{putPriceChange.toFixed(2)}%
          </span>
        </div>
      </div>
    );
  };

  if (isLoading) {
    return (
      <Box className={`${darkMode ? 'dark-mode' : ''}`} sx={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: 'calc(100vh - 64px)',
        flexDirection: 'column',
        gap: 2,
        backgroundColor: darkMode ? '#121212' : '#f5f5f5',
        color: darkMode ? '#fff' : '#333'
      }}>
        <CircularProgress color="primary" />
        <Typography variant="h6">Loading option chain data...</Typography>
        <FormControlLabel
          control={<Switch checked={darkMode} onChange={toggleDarkMode} />}
          label="Dark Mode"
        />
      </Box>
    );
  }

  if (error) {
    return (
      <Box className={`${darkMode ? 'dark-mode' : ''}`} sx={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: 'calc(100vh - 64px)',
        flexDirection: 'column',
        gap: 2,
        backgroundColor: darkMode ? '#121212' : '#f5f5f5',
        color: darkMode ? '#fff' : '#333'
      }}>
        <Typography variant="h6" color="error">{error}</Typography>
        <Typography variant="body1">Please try refreshing the page.</Typography>
        <FormControlLabel
          control={<Switch checked={darkMode} onChange={toggleDarkMode} />}
          label="Dark Mode"
        />
      </Box>
    );
  }

  const underlyingData = { currentPrice: underlyingPrice, percentChange: 0 };

  return (
    <Box className={`option-chain-container ${darkMode ? 'dark-mode' : ''}`} sx={{
      height: 'calc(100vh - 64px)',
      display: 'flex',
      flexDirection: 'column',
      backgroundColor: darkMode ? '#121212' : '#f5f5f5',
      color: darkMode ? '#fff' : '#333'
    }}>
      <Box sx={{ 
        p: 2,
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        borderBottom: '1px solid',
        borderColor: darkMode ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.1)',
        backgroundColor: darkMode ? '#1e1e1e' : '#fff'
      }}>
        <Box>
          <Typography variant="h5" sx={{ fontWeight: 'bold' }}>
            {optionChainData.symbol} Option Chain
          </Typography>
          {underlyingData && (
            <Box sx={{ display: 'flex', alignItems: 'center', mt: 0.5 }}>
              <Typography variant="h6" sx={{ fontWeight: 'bold', mr: 1 }}>
                {formatPrice(underlyingData.currentPrice)}
              </Typography>
              <Typography 
                variant="body1" 
                sx={{ 
                  color: underlyingData.percentChange >= 0 ? '#4caf50' : '#f44336',
                  fontWeight: 500
                }}
              >
                {underlyingData.percentChange >= 0 ? '+' : ''}{underlyingData.percentChange.toFixed(2)}%
              </Typography>
            </Box>
          )}
        </Box>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Chip 
            label={isConnected ? "Connected" : reconnecting ? "Reconnecting..." : "Disconnected"} 
            color={isConnected ? "success" : reconnecting ? "warning" : "error"}
            size="small"
          />
          <FormControlLabel
            control={<Switch size="small" checked={darkMode} onChange={toggleDarkMode} />}
            label="Dark Mode"
          />
        </Box>
      </Box>

      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'center', 
        p: 1, 
        backgroundColor: darkMode ? 'rgba(255,255,255,0.05)' : 'rgba(0,0,0,0.03)'
      }}>
        <Box sx={{ display: 'flex', width: '100%', maxWidth: '800px' }}>
          <Typography variant="body1" sx={{ flex: 1, textAlign: 'center', fontWeight: 'bold' }}>Call</Typography>
          <Typography variant="body1" sx={{ width: '80px', textAlign: 'center', fontWeight: 'bold' }}>Strike</Typography>
          <Typography variant="body1" sx={{ flex: 1, textAlign: 'center', fontWeight: 'bold' }}>Put</Typography>
        </Box>
      </Box>

      <Box sx={{ flex: 1 }}>
        {optionData && optionData.strikes && (
          <AutoSizer>
            {({ height, width }) => (
              <List
                width={width}
                height={height}
                rowCount={optionData.strikes.length}
                rowHeight={60}
                rowRenderer={renderRow}
                overscanRowCount={10}
                className={darkMode ? 'dark-mode-list' : ''}
              />
            )}
          </AutoSizer>
        )}
      </Box>
    </Box>
  );
};

export default OptionChain;
